INSERT INTO `posts` (`id`, `title`, `image`, `info`, `content`, `status`, `date_created`, `cate_id`, `facebook`, `video`, `seo_link`, `seo_title`, `seo_keyword`, `seo_description`) VALUES
(2, 'Đánh giá ưu - nhược điểm của Tivi LG có nên mua ?', 'danh-gia-uu-nhuoc-diem-cua-tivi-lg-co-nen-mua-1_5886398879543.jpg', '<p>Gần đ&acirc;y điện m&aacute;y Trần Anh nhận được kh&aacute; nhiều c&acirc;u hỏi từ kh&aacute;ch h&agrave;ng li&ecirc;n quan đến vấn đề c&oacute; n&ecirc;n mua Tivi LG kh&ocirc;ng ? Để giải đ&aacute;p những thắc mắc n&agrave;y, điện m&aacute;y Trần Anh xin chia sẻ đến &nbsp;người d&ugrave;ng b&agrave;i viết &ldquo;Đ&aacute;nh gi&aacute; ưu - nhược điểm của Tivi LG c&oacute; n&ecirc;n mua ?&rdquo;</p>', '<p>LG l&agrave; h&atilde;ng sản xuất Tivi nổi tiếng v&agrave; l&acirc;u đời mức độ uy t&iacute;n c&aacute;c gia đ&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m, LG lu&ocirc;n l&agrave; Top thương hiệu Tivi uy t&iacute;n kh&ocirc;ng hề thua k&eacute;m c&aacute;c đơn vị sản xuất kh&aacute;c như Tivi Samsung, Tivi Sharp, hay Tivi Panasonic&hellip; Vậy Tivi LG c&oacute; những ưu điểm nổi trội g&igrave; m&agrave; khiến cho nhiều kh&aacute;ch h&agrave;ng bỏ qua c&aacute;c thương hiệu nổi tiếng kh&aacute;c như Sony, Panasonic,&hellip; C&ugrave;ng điện m&aacute;y Trần Anh t&igrave;m hiểu vấn đề n&agrave;y qua b&agrave;i viết dưới đ&acirc;y nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/20/danh-gia-uu-nhuoc-diem-cua-tivi-lg-co-nen-mua-.jpg" /></p>\r\n\r\n<p>Đ&aacute;nh gi&aacute; ưu - nhược điểm của Tivi LG c&oacute; n&ecirc;n mua ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ưu - Nhược điểm của Tivi LG.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh đa dạng: Để phục vụ cho nhu cầu sử dụng c&aacute; nh&acirc;n th&igrave; Tivi LG c&oacute; c&aacute;c k&iacute;ch thước từ 24 inch đến 47 inch. Với những sản phẩm cao cấp hơn th&igrave; c&oacute; k&iacute;ch thước lớn hơn, từ 55 inch đến 80 inch, bạn c&oacute; thể lựa chọn t&ugrave;y theo nhu cầu sử dụng v&agrave; kh&ocirc;ng gian của căn ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tấm nền m&agrave;n h&igrave;nh IPS bền: C&aacute;c sản phẩm Tivi của LG sử dụng tấm nền m&agrave;n h&igrave;nh IPS n&ecirc;n bền v&agrave; ổn định hơn so với c&aacute;c loại m&agrave;n h&igrave;nh th&ocirc;ng thường kh&aacute;c. Mua Tivi LG gi&uacute;p cho bạn thưởng thức được m&agrave;u sắc trung thực gần với tự nhi&ecirc;n nhất, g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chip xử l&yacute; h&igrave;nh ảnh Triple XD Engine: Ch&iacute;p h&igrave;nh ảnh n&agrave;y được LG sử dụng độc quyền khiến Tivi ho&agrave;n chỉnh m&agrave;u sắc hơn, cho độ tương phản v&agrave; c&oacute; độ r&otilde; n&eacute;t nhất. Do đ&oacute; m&agrave; cho d&ugrave; đang ph&aacute;t ở nguồn n&agrave;o, từ h&igrave;nh ảnh trong phim hay game đồ họa cao th&igrave; vẫn được xử l&yacute; mạnh mẽ đem đến trải nghiệm r&otilde; n&eacute;t nhất cho người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiển thị h&igrave;nh ảnh 4K cực n&eacute;t: Độ ph&acirc;n giải 4K c&oacute; độ n&eacute;t cao hơn Full HD đến 4 lần n&ecirc;n mua Tivi LG sẽ gi&uacute;p cho người xem c&oacute; c&aacute;i nh&igrave;n ch&acirc;n thực v&agrave; sống động nhất. Bạn đ&atilde; biết top Tivi LG 4k hot nhất hiện nay chưa, m&ugrave;a b&oacute;ng đ&aacute;, thể thao m&agrave; kh&ocirc;ng c&oacute; một chiếc Tivi LG 4k th&igrave; sẽ rất hối tiếc đ&oacute;, sắm ngay một em đi nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh cong OLED: LG l&agrave; nh&agrave; sản xuất ti&ecirc;n phong trong thương mại h&oacute;a c&ocirc;ng nghệ OLED v&agrave;o Tivi . Ch&iacute;nh v&igrave; thế m&agrave; c&aacute;c mẫu Tivi của LG c&oacute; m&agrave;n h&igrave;nh si&ecirc;u mỏng, cho h&igrave;nh ảnh đẹp c&ugrave;ng khả năng tiết kiệm điện năng hiệu quả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/20/danh-gia-uu-nhuoc-diem-cua-tivi-lg-co-nen-mua--2.jpg" /></p>\r\n\r\n<p>Tivi LG gi&aacute; th&agrave;nh ph&ugrave; hợp với nhiều gia đ&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xem 3D kh&ocirc;ng rung h&igrave;nh: Như c&aacute;c bạn đ&atilde; biết th&igrave; khi xem phim 3D h&igrave;nh ảnh thường bị rung g&acirc;y nhức mỏi mắt, tuy nhi&ecirc;n với c&ocirc;ng nghệ 3D thụ động của LG th&igrave; điều n&agrave;y sẽ kh&ocirc;ng xảy ra nữa. Người xem c&oacute; thể thưởng thức những h&igrave;nh ảnh 3D ch&acirc;n thực v&agrave; sống động nhất. Kh&ocirc;ng những vậy m&agrave; bạn c&ograve;n c&oacute; thể giả lập h&igrave;nh ảnh 2D sang 3D nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kết nối đa dạng: Tivi LG c&oacute; kết nối đa dạng c&oacute; thể kể đến như HDMI, USB xem phim, nghe nhạc. Ngo&agrave;i ra th&igrave; c&aacute;c Smart Tivi c&ograve;n c&oacute; thể thực hiện c&aacute;c kết nối kh&ocirc;ng d&acirc;y, v&igrave; vậy bạn sẽ c&oacute; th&ecirc;m nhiều lựa chọn giải tr&iacute; đặc sắc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c địa chỉ bảo h&agrave;nh Tivi LG được ph&acirc;n bố rộng khắp: Trong qu&aacute; tr&igrave;nh sử dụng nếu bạn găp bất cứ vấn đề g&igrave; cũng c&oacute; thể nhanh ch&oacute;ng đến c&aacute;c cơ sở bảo h&agrave;nh Tivi LG để khắc phục v&agrave; sửa chữa nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/20/danh-gia-uu-nhuoc-diem-cua-tivi-lg-co-nen-mua-1.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tivi LG trang bị c&aacute;c c&ocirc;ng nghệ Tivi mới nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tivi LG đa dạng về chủng loại v&agrave; kh&aacute; hợp v&iacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Điện m&aacute;y Trần Anh</strong> xin giới thiệu đến người d&ugrave;ng 3 d&ograve;ng sản phẩm với 3 mức gi&aacute; kh&aacute;c nhau, ph&ugrave; hợp với từng điều kiện t&agrave;i ch&iacute;nh của c&aacute;c gia đ&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Đặc điểm</p>\r\n			</td>\r\n			<td>\r\n			<p>Tivi LED LG 32LF550D 32inch</p>\r\n			</td>\r\n			<td>\r\n			<p>Smart Tivi LG 32LF595D 32inch HD</p>\r\n			</td>\r\n			<td>\r\n			<p>Smart Tivi LG 60LF632T 60inch</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Loại Tivi</p>\r\n			</td>\r\n			<td>\r\n			<p>Tivi LED.</p>\r\n			</td>\r\n			<td>\r\n			<p>Smart Tivi.</p>\r\n			</td>\r\n			<td>\r\n			<p>Smart Tivi.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Gi&aacute; th&agrave;nh (VNĐ)</p>\r\n			</td>\r\n			<td>\r\n			<p>4,490,000</p>\r\n			</td>\r\n			<td>\r\n			<p>7,000,000</p>\r\n			</td>\r\n			<td>\r\n			<p>23,990,000</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>M&agrave;n h&igrave;nh</p>\r\n			</td>\r\n			<td>\r\n			<p>32 inch.</p>\r\n			</td>\r\n			<td>\r\n			<p>32 inch.</p>\r\n			</td>\r\n			<td>\r\n			<p>60 inch.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Độ ph&acirc;n giải</p>\r\n			</td>\r\n			<td>\r\n			<p>1366x768</p>\r\n			</td>\r\n			<td>\r\n			<p>1366x768</p>\r\n			</td>\r\n			<td>\r\n			<p>1920x1080</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Kết nối</p>\r\n			</td>\r\n			<td>\r\n			<p>- 2 Cổng HDMI , 2 cổng USB</p>\r\n\r\n			<p>- Cổng AV</p>\r\n\r\n			<p>- Kh&ocirc;ng kết nối wifi.</p>\r\n\r\n			<p>- Cổng Anten.</p>\r\n			</td>\r\n			<td>\r\n			<p>- 2 Cổng HDMI, 2 cổng USB</p>\r\n\r\n			<p>- Cổng AV</p>\r\n\r\n			<p>- Kết nối wifi</p>\r\n\r\n			<p>- Cổng Anten.</p>\r\n			</td>\r\n			<td>\r\n			<p>- 3 cổng HDMI. 3 cổng USB</p>\r\n\r\n			<p>- Cổng AV</p>\r\n\r\n			<p>- Kết nối wifi</p>\r\n\r\n			<p>- Cổng Anten.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&ocirc;ng nghệ</p>\r\n			</td>\r\n			<td>\r\n			<p>- C&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh Triple XD Engine.</p>\r\n\r\n			<p>- &Acirc;m thanh v&ograve;m sống động.</p>\r\n			</td>\r\n			<td>\r\n			<p>- Chip xử l&yacute; Triple XD Engine</p>\r\n\r\n			<p>- &Acirc;m thanh v&ograve;m sống động</p>\r\n			</td>\r\n			<td>\r\n			<p>- Smart TV chạy HĐH WebOS bộ nhớ trong 8 Gb.</p>\r\n\r\n			<p>- C&ocirc;ng nghệ h&igrave;nh ảnh mới nhất cho độ n&eacute;t : 1100PMI.</p>\r\n\r\n			<p>- C&ocirc;ng nghệ &acirc;m thanh v&ograve;m sống động.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>T&iacute;nh năng</p>\r\n			</td>\r\n			<td>\r\n			<p>- Kết nối Mobile Link với điện thoại.</p>\r\n\r\n			<p>- T&iacute;ch hợp KTS DVB-T2.</p>\r\n\r\n			<p>- Time Machine lưu giữ những khoảnh khắc hấp dẫn.</p>\r\n			</td>\r\n			<td>\r\n			<p>- Chia sẻ th&ocirc;ng minh. (Miracast, Widi, WiFi Direct)</p>\r\n\r\n			<p>- T&iacute;ch hợp KTS DVB-T2.</p>\r\n\r\n			<p>- Giải tr&iacute; tr&ecirc;n Internet</p>\r\n			</td>\r\n			<td>\r\n			<p>- Ghi h&igrave;nh</p>\r\n\r\n			<p>- Chia sẻ th&ocirc;ng minh (Miracast, Widi, WiFi Direct)</p>\r\n\r\n			<p>- Wifi t&iacute;ch hợp</p>\r\n\r\n			<p>- Ứng dụng giải tr&iacute; đa dạng.</p>\r\n\r\n			<p>- T&iacute;ch hợp KTS DVB-T2.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Tần số qu&eacute;t</p>\r\n			</td>\r\n			<td>\r\n			<p>200Hz</p>\r\n			</td>\r\n			<td>\r\n			<p>100Hz</p>\r\n			</td>\r\n			<td>\r\n			<p>100Hz</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&ocirc;ng suất</p>\r\n			</td>\r\n			<td>\r\n			<p>65W</p>\r\n			</td>\r\n			<td>\r\n			<p>65W</p>\r\n			</td>\r\n			<td>\r\n			<p>150W</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', 1, '2017-01-23 11:12:40', 64, NULL, '', NULL, NULL, NULL, NULL),
(3, 'Đánh giá Tivi Samsung 43K5500AK có nên mua ?', 'danh-gia-tivi-samsung-43k5500ak-co-nen-mua-1_588639f91b5f8.jpg', '<p><strong>Samsung được biết đến l&agrave; một thương hiệu lớn c&oacute; uy t&iacute;n tr&ecirc;n thị trường với c&aacute;c sản phẩm chất lượng tốt v&agrave; gi&aacute; th&agrave;nh hợp l&yacute;. Một trong những sản phẩm nổi bật của Samsung l&agrave; Smart Tivi Samsung 43K5500AK với thiết kế tinh tế sang trọng v&agrave; t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ hiện đại Samsung 43K5500AK nhận được sự quan t&acirc;m từ người d&ugrave;ng. C&ugrave;ng điện m&aacute;y Trần Anh đ&aacute;nh gi&aacute; chi tiết Tivi Samsung UA43K5500AK c&oacute; n&ecirc;n mua ?</strong></p>', '<p><strong><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/10/danh-gia-tivi-samsung-43k5500ak-co-nen-mua-1.jpg" />&nbsp;</strong></p>\r\n\r\n<p>C&oacute; n&ecirc;n mua Smart Tivi Samsung 43K5500AK.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đặc điểm nổi bật của Smart Tivi Samsung 43K5500AK.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&oacute; thiết kế mỏng, sang trọng, hiện đại v&agrave; c&oacute; độ bền cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh 43 inch c&oacute; độ ph&acirc;n giải Full HD độ ph&acirc;n giải cao gấp đ&ocirc;i chuẩn tivi HD gi&uacute;p bạn thưởng thức chương tr&igrave;nh truyền h&igrave;nh y&ecirc;u th&iacute;ch v&agrave; phim ảnh với một trải nghiệm đầy đủ độ n&eacute;t cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Sử dụng vi xử l&yacute; Chip Quad core cho ph&eacute;p Tivi chạy hệ điều h&agrave;nh Tizen nhanh v&agrave; mượt. C&aacute;c l&otilde;i được thiết lập để xử l&yacute; c&ugrave;ng l&uacute;c mới nhiệm vụ nhất định, gi&uacute;p c&aacute;c lệnh trong ứng dụng được xử l&yacute; nhanh ch&oacute;ng, chạy đa nhiệm, tốc độ cải thiện hơn.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hệ điều h&agrave;nh Tizen với giao diện than thiện dễ sử dụng v&agrave; c&oacute; kho ứng dụng giải tr&iacute; đa dạng phong ph&uacute; mang đến trải nghiệm mới cho người d&ugrave;ng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/10/danh-gia-tivi-samsung-43k5500ak-co-nen-mua-2.jpg" />&nbsp;</p>\r\n\r\n<p>Smart Tivi Samsung 43K5500AK trang bị m&agrave;n h&igrave;nh Full HD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Sử dụng c&ocirc;ng nghệ Digital Clean View l&agrave; c&ocirc;ng nghệ xử l&yacute; nhiễu kỹ thuật số của&nbsp;<strong>Tivi Samsung</strong>, c&oacute; t&aacute;c dụng tối ưu h&oacute;a chất lượng h&igrave;nh ảnh, loại bỏ ho&agrave;n to&agrave;n hiện tượng nhiễu h&igrave;nh ảnh gi&uacute;p bạn c&oacute; thể cảm nhận được sự mịn m&agrave;ng trong từng chi tiết, hiển thị ch&acirc;n thực r&otilde; n&eacute;t trung thực đến tuyệt đối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- T&iacute;ch hợp c&ocirc;ng nghệ Micro Dimming Pro l&agrave; c&ocirc;ng nghệ xử l&yacute; hỉnh ảnh c&oacute; khảng năng chia đ&ocirc;i v&agrave; tinh chỉnh chi tiết từng khối h&igrave;nh ảnh nhỏ cho ph&eacute;p ph&acirc;n t&iacute;ch v&agrave; xử l&yacute; độ tương phản &aacute;nh s&aacute;ng v&agrave; m&agrave;u sắc tốt hơn. Người xem sẽ được thưởng thức từng khung h&igrave;nh với độ ph&acirc;n giải tối đa v&agrave; m&agrave;u sắc r&otilde; n&eacute;t sống động hơn bao giờ hết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&ocirc;ng nghệ DTS Studio Sound cho ph&eacute;p t&aacute;i tạo một c&aacute;ch ch&iacute;nh x&aacute;c c&aacute;c t&iacute;n hiệu &acirc;m thanh nổi từ nguồn ph&aacute;t. Với c&ocirc;ng nghệ n&agrave;y Tivi sẽ ph&aacute;t ra những &acirc;m thanh c&oacute; độ trầm v&agrave; lời thoại r&otilde; hơn, c&acirc;n bằng &acirc;m lượng c&aacute;c k&ecirc;nh tivi khi chuyển k&ecirc;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/10/danh-gia-tivi-samsung-43k5500ak-co-nen-mua-3.jpg" />&nbsp;</p>\r\n\r\n<p>Smart Tivi Samsung 43K5500AK đầy đủ c&aacute;c kết nối với thiết bị ngoại vi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&ocirc;ng nghệ &acirc;m thanh DTS Premium Sound 5.1 c&oacute; khả năng lọc li&ecirc;n tục, th&ecirc;m bass để tạo n&ecirc;n chiều s&acirc;u v&agrave; ho&agrave;n chỉnh c&aacute;c &acirc;m thanh từ c&aacute;c nguồn ph&aacute;t v&agrave; mang đến cho người nghe những &acirc;m thanh r&otilde; n&eacute;t v&agrave; ho&agrave;nh tr&aacute;ng hơn gấp nhiều lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Trang bị đầu thu KTS DVB - T2 đem lại những chương tr&igrave;nh miễn ph&iacute; chất lượng giải tr&iacute; cao.Ngo&agrave;i ra, c&aacute;c cổng kết nối USB, HDMI, cổng LAN v&agrave; kết nối wifi cho ph&eacute;p sử dụng nhiều t&aacute;c vụ kh&aacute;c nhau.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nhược điểm ở Smart Tivi Samsung 43K5500AK.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- K&iacute;ch thước m&agrave;n h&igrave;nh lớn n&ecirc;n kh&ocirc;ng ph&ugrave; hợp với những gia đ&igrave;nh c&oacute; kh&ocirc;ng gian nhỏ hẹp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Thiết kế của tivi c&ograve;n kh&aacute; d&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n những nhược điểm n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể khắc phục được. Samsung c&oacute; 3 t&ugrave;y chọn về m&agrave;n h&igrave;nh ở&nbsp;<strong>Smart Tivi 43K5500AK&nbsp;</strong>: 32 inch, 40 inch, 49 inch, 55 inch. Với những t&ugrave;y chọn đ&oacute; t&ugrave;y thuộc v&agrave;o nhu cầu v&agrave; kh&ocirc;ng gian m&agrave; người d&ugrave;ng c&oacute; thể lựa chọn được một chiếc&nbsp;<strong>Smart Tivi Samsung</strong>&nbsp;ph&ugrave; hợp nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/10/danh-gia-tivi-samsung-43k5500ak-co-nen-mua-4.jpg" />&nbsp;</p>\r\n\r\n<p>Smart Tivi Samsung 43K5500AK kết nối với điện thoại th&ocirc;ng minh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&oacute; n&ecirc;n mua Smart Tivi Samsung 43K5500AK kh&ocirc;ng?</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong tầm gi&aacute; 10 triệu đồng, Smart Tivi Samsung 43K5500AK l&agrave; một trong những sản phẩm đ&aacute;ng được lựa chọn h&agrave;ng đầu hiện nay bởi chất lượng tốt, c&ocirc;ng nghệ hiện đại, ti&ecirc;n tiến, đem đến khả năng xử l&yacute; h&igrave;nh ảnh trung thực, b&ecirc;n cạnh đ&oacute;, chiếc tivi n&agrave;y c&ograve;n đa dạng nhiều cổng kết nối, &acirc;m thanh sống động, mạnh mẽ...gi&uacute;p gia đ&igrave;nh bạn c&oacute; kh&ocirc;ng gian giải tr&iacute; tốt nhất. Ngo&agrave;i ra, với 3 t&ugrave;y chọn về m&agrave;n h&igrave;nh ở Smart Tivi 43K5500AK : 32 inch, 40 inch, 49 inch, 55 inch. Bạn c&oacute; thể lựa chọn cho gia đ&igrave;nh một chiếc Tivi ph&ugrave; hợp nhất cho kh&ocirc;ng gian sống. S<strong>mart Tivi Samsung</strong>&nbsp;lu&ocirc;n được đ&aacute;nh gi&aacute; cao bởi chất lượng ổn định v&agrave; gi&aacute; th&agrave;nh ph&ugrave; hợp với mọi nh&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; b&agrave;i&nbsp;<strong>đ&aacute;nh gi&aacute; Smart Tivi Samsung 43K5500AK</strong>, nếu bạn cảm thấy đ&acirc;y l&agrave; một sản phẩm ph&ugrave; hợp với gia đ&igrave;nh bạn th&igrave; h&atilde;y nhanh ch&acirc;n đến những địa điểm b&aacute;n h&agrave;ng c&oacute; uy t&iacute;n, đảm bảo để mua được một sản phẩm ch&iacute;nh h&atilde;ng v&agrave; c&oacute; chất lượng tốt nhất để sử dụng.</p>', 1, '2017-01-23 11:14:33', 64, NULL, '', NULL, NULL, NULL, NULL),
(4, 'Tivi 32 inch - Nên mua Smart Tivi Sony KDL-32W600D hay Tivi LED Toshiba 32L3650 HD', 'tivi-32-inch-nen-mua-smart-tivi-sony-kdl-32w600d-hay-tivi-led-toshiba-32l3650-hd-2_58863a8548b28.jpg', '<p>Tivi 32 inch - N&ecirc;n mua Smart Tivi Sony KDL-32W600D hay Tivi LED Toshiba 32L3650 HD ? Đ&acirc;y c&oacute; lẽ cũng đang l&agrave; nỗi ph&acirc;n v&acirc;n của rất nhiều người ti&ecirc;u d&ugrave;ng. Cả hai đại diện đến từ hai thương hiệu Tivi lớn n&agrave;y đều c&oacute; những ưu nhược điểm ri&ecirc;ng. H&atilde;y c&ugrave;ng trananh.vn so s&aacute;nh hai d&ograve;ng Tivi n&agrave;y để lựa chọn được sản phẩm ph&ugrave; hợp nh&eacute;.</p>', '<p>Bảng so s&aacute;nh một số th&ocirc;ng tin kỹ thuật của hai d&ograve;ng sản phẩm: Smart Tivi Sony KDL-32W600D v&agrave; Tivi LED Toshiba 32L3650 HD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Đặc điểm so s&aacute;nh</p>\r\n			</td>\r\n			<td>\r\n			<p>Sony KDL-32W600D</p>\r\n			</td>\r\n			<td>\r\n			<p>Toshiba 32L3650 HD</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Loại Tivi</p>\r\n			</td>\r\n			<td>\r\n			<p>Tivi LED</p>\r\n			</td>\r\n			<td>\r\n			<p>Tivi LED</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>K&iacute;ch cỡ m&agrave;n h&igrave;nh</p>\r\n			</td>\r\n			<td>\r\n			<p>32 inch</p>\r\n			</td>\r\n			<td>\r\n			<p>32 inch</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Độ ph&acirc;n giải</p>\r\n			</td>\r\n			<td>\r\n			<p>1366x768</p>\r\n			</td>\r\n			<td>\r\n			<p>1366x768</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cổng kết nối</p>\r\n			</td>\r\n			<td>\r\n			<p>- Kết nối Internet</p>\r\n\r\n			<p>- 2 cổng HDMI</p>\r\n\r\n			<p>- Cổng xuất &acirc;m thanh Jack 3.5 mm, cổng Optical (Digital Audio Out)</p>\r\n\r\n			<p>- 2 cổng USB</p>\r\n			</td>\r\n			<td>\r\n			<p>- 2 cổng HDMI</p>\r\n\r\n			<p>- Cổng AV</p>\r\n\r\n			<p>- Kh&ocirc;ng kết nối wifi</p>\r\n\r\n			<p>- Cổng Antenn</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&ocirc;ng nghệ h&igrave;nh ảnh, &acirc;m thanh</p>\r\n			</td>\r\n			<td>\r\n			<p>C&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh:X-Reality PRO</p>\r\n			</td>\r\n			<td>\r\n			<p>CEVO Engine,</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>T&iacute;nh năng</p>\r\n			</td>\r\n			<td>\r\n			<p>- Kho giải tr&iacute; với Youtube, Internet...</p>\r\n\r\n			<p>- C&aacute;c ứng dụng giải tr&iacute; kh&aacute;c.</p>\r\n\r\n			<p>- T&iacute;ch hợp đầu thu DVB-T2</p>\r\n\r\n			<p>- Screen Mirroring, Photo Share Plus,&hellip;</p>\r\n			</td>\r\n			<td>\r\n			<p>-T&iacute;ch hợp KTS DVB-T2</p>\r\n\r\n			<p>- C&oacute; kết nối MHL</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Tần số qu&eacute;t</p>\r\n			</td>\r\n			<td>\r\n			<p>200Hz</p>\r\n			</td>\r\n			<td>\r\n			<p>100Hz</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&ocirc;ng suất ti&ecirc;u thụ điện</p>\r\n			</td>\r\n			<td>\r\n			<p>45W</p>\r\n			</td>\r\n			<td>\r\n			<p>65W</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&igrave;n chung, trong d&ograve;ng sản phẩm Tivi 32 inch, cả Smart Tivi Sony KDL-32W600D v&agrave; Toshiba 32L3650VN đều l&agrave; d&ograve;ng tivi LED kh&aacute; nổi trội tr&ecirc;n thị trường, c&ugrave;ng với đ&oacute; l&agrave; m&agrave;n h&igrave;nh 32 inch th&iacute;ch hợp với mọi kh&ocirc;ng gian gia đ&igrave;nh kh&aacute;c nhau. Tuy nhi&ecirc;n, Tivi Sony KDL-32W600D c&oacute; nhiều n&eacute;t nổi bật hơn như khả năng kết nối Internet, tần số qu&eacute;t lớn hơn gi&uacute;p người d&ugrave;ng c&oacute; được sự trải nghiệm h&igrave;nh ảnh, &acirc;m thanh r&otilde; n&eacute;t nhất, ch&acirc;n thực nhất. B&ecirc;n cạnh đ&oacute;, tivi Sony KDL-32W600D cũng sở hữu thiết kế đẹp mắt, sang trọng, hiện đại của d&ograve;ng Tivi LED của h&atilde;ng Sony.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/20/tivi-32-inch-nen-mua-smart-tivi-sony-kdl-32w600d-hay-tivi-led-toshiba-32l3650-hd-2.jpg" /></p>\r\n\r\n<p>Smart Tivi Sony KDL-32W600D c&oacute; gi&aacute; 7.490.000 VNĐ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, nếu so s&aacute;nh gi&aacute; của hai d&ograve;ng Tivi n&agrave;y th&igrave; Smart Tivi Sony KDL-32W600D c&oacute; mức gi&aacute; cao hơn hẳn 3 triệu đồng, do được t&iacute;ch hợp nhiều t&iacute;nh năng v&agrave; c&ocirc;ng nghệ mới hơn. Tivi Led Toshiba 32L3650 HD được trang bị m&agrave;n h&igrave;nh với độ ph&acirc;n giải 1366x768 cho h&igrave;nh ảnh sắc n&eacute;t c&ugrave;ng những t&iacute;nh năng nổi trội đ&aacute;p ứng được c&aacute;c nhu cầu giải tr&iacute; cơ bản cho c&aacute;c gia đ&igrave;nh. Hơn thế nữa, mức gi&aacute; của Tivi Led Toshiba 32L3650 HD cũng kh&aacute; hợp l&yacute; với những gia đ&igrave;nh c&oacute; mức t&agrave;i ch&iacute;nh trung b&igrave;nh hiện nay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/20/tivi-32-inch-nen-mua-smart-tivi-sony-kdl-32w600d-hay-tivi-led-toshiba-32l3650-hd-1.jpg" /></p>\r\n\r\n<p>Tivi Led Toshiba 32L3650 HD 4.890.000 VNĐ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qua những th&ocirc;ng tin so s&aacute;nh tr&ecirc;n, hi vọng vấn đề n&ecirc;n mua Smart Tivi Sony KDL-32W600D hay Toshiba 32L3650 HD đ&atilde; trở n&ecirc;n đơn giản hơn rất nhiều, nếu bạn y&ecirc;u th&iacute;ch sản phẩm n&agrave;o, bạn c&oacute; thể đến những cửa h&agrave;ng ph&acirc;n phối ch&iacute;nh h&atilde;ng hoặc những địa điểm b&aacute;n h&agrave;ng uy t&iacute;n để sở hữu những sản phẩm c&oacute; chất lượng tốt nhất. Tivi Sony lu&ocirc;n nổi bật bởi thiết kế đẹp mắt, sang trọng, tiện nghi v&agrave; t&iacute;nh năng sử dụng kh&aacute; ổn định, b&ecirc;n cạnh đ&oacute;, Tivi Led Toshiba cũng kh&ocirc;ng k&eacute;m cạnh khi c&aacute;c sản phẩm được sản xuất tr&ecirc;n d&acirc;y chuyền c&ocirc;ng nghệ hiện đại, sở hữu những t&iacute;nh năng h&agrave;ng đầu về c&ocirc;ng nghệ Tivi hiện nay.</p>\r\n\r\n<p>&nbsp;</p>', 2, '2017-01-23 11:16:53', 65, NULL, '', NULL, NULL, NULL, NULL),
(5, '4 lưu ý khi chọn mua đầu DVD chất lượng tốt.', '4-luu-y-khi-chon-mua-dau-dvd-chat-luong-tot-2_58863abe96ade.jpg', '<p><strong>Đầu DVD hiện nay c&oacute; rất nhiều loại với nhiều t&iacute;nh năng kh&aacute;c nhau, vậy lựa chọn sản phẩm n&agrave;o l&agrave; tốt v&agrave; ph&ugrave; hợp nhất. Nếu bạn đang băn khoăn chọn mua đầu DVD, h&atilde;y tham khảo 4 lưu &yacute; khi chọn mua đầu DVD &nbsp;trong b&agrave;i viết dưới đ&acirc;y nh&eacute;.</strong></p>', '<p><strong><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/09/4-luu-y-khi-chon-mua-dau-dvd-chat-luong-tot-2.jpg" style="height:178px; width:600px" />&nbsp;</strong></p>\r\n\r\n<p>Chọn mua chiếc đầu DVD ph&ugrave; hợp với nhu cầu sử dụng của gia đ&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Khi chọn mua đầu DVD n&ecirc;n ch&uacute; &yacute; đến khả năng t&agrave;i ch&iacute;nh.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute; th&agrave;nh của chiếc đầu DVD l&agrave; điểm lưu &yacute; đầu ti&ecirc;n m&agrave; người ti&ecirc;u d&ugrave;ng cần lưu &yacute; khi chọn mua bất kỳ một sản phẩm n&agrave;o. Mua đầu DVD gi&aacute; tốt sẽ tiết kiệm cho ph&iacute; cho người d&ugrave;ng. Đầu DVD c&oacute; gi&aacute; b&aacute;n kh&ocirc;ng qu&aacute; cao nhưng n&oacute; cũng c&oacute; nhiều mức gi&aacute; cho bạn lựa chọn. T&ugrave;y thuộc v&agrave;o nhu cầu sử dụng của gia đ&igrave;nh m&agrave; bạn chọn mua một chiếc đầu DVD ph&ugrave; hợp. Nếu gia đ&igrave;nh bạn c&oacute; nhu cầu sử dụng đơn thuần như xem phim, nghe nhạc th&igrave; c&oacute; thể tham khảo c&aacute;c loại đầu DVD c&oacute; gi&aacute; khoảng 500.000 VNĐ.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c định dạng chuẩn của đầu DVD.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng số tiếp theo bạn cần lưu &yacute; đ&oacute; l&agrave; c&aacute;c định dạng chuẩn của đầu DVD. Bạn cần mua loại đầu DVD c&oacute; khả năng đọc được nhiều định dạng hay &iacute;t. Đầu DVD c&oacute; c&aacute;c định dạng cho ph&eacute;p ghi 1 lần hay nhiều lần. C&aacute;c k&yacute; hiệu DVD-R, DVD+R, DVD-RW, DVD+RW tr&ecirc;n đầu DVD cho biết đầu DVD đọc được c&aacute;c loại đĩa định dạng n&agrave;y. DVD-R v&agrave; DVD+R l&agrave; c&aacute;c định dạng cho ph&eacute;p ghi đĩa một lần. DVD-RW v&agrave; DVD+RW l&agrave; c&aacute;c định dạng cho ph&eacute;p ghi nhiều lần. Một số đầu đĩa DVD c&ograve;n c&oacute; khả năng đọc cả định dạng DVD-RAM, d&ugrave; tr&ecirc;n thị trường hiện nay số đầu DVD c&oacute; khả năng n&agrave;y rất &iacute;t. C&aacute;c đầu DVD đều c&oacute; khả năng đọc định dạng Video-CD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lưu &yacute; đến định dạng Audio của đầu DVD.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Định dạng Audio cũng l&agrave; một lưu &yacute; quan trọng khi chọn mua đầu DVD. Bạn h&atilde;y chọn mua một chiếc đầu DVD c&oacute; khả năng đọc định dạng &acirc;m thanh MP3 v&agrave; WMA ph&ocirc;̉ bi&ecirc;́n tr&ecirc;n thị trường. Hiện nay tất cả c&aacute;c b&agrave;i h&aacute;t, file nhạc đều được định dạng mp3, định dạng WMA tuy k&eacute;m ph&ocirc;̉ bi&ecirc;́n hơn nhưng lại c&oacute; ti&ecirc;̀m năng r&acirc;́t lớn v&igrave; được Microsoft li&ecirc;n tục ho&agrave;n thiện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/09/4-luu-y-khi-chon-mua-dau-dvd-chat-luong-tot.jpg" style="height:300px; width:600px" /></p>\r\n\r\n<p>Chọn mua đầu DVD lưu &yacute; đến định dạng audio v&agrave; định dạng ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lưu &yacute; đến định dạng ảnh của đầu DVD.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng l&agrave; định dạng ảnh, một yếu tố quan trọng kh&ocirc;ng k&eacute;m so với những yếu tố tr&ecirc;n. Một chiếc đầu DVD tốt cần c&oacute; định dạng ảnh đa dạng, ph&ugrave; hợp với nhu cầu sử dụng cơ bản của mọi người. Đa số c&aacute;c đầu DVD hiện đại hiện nay đều tập trung hỗ trợ định dạng Kodak Photo CD hoặc Kodak Picture CD d&ugrave; vẫn c&oacute; một số đầu DVD hỗ trợ định dạng ảnh JPEG.Nếu chỉ đơn giản để xem ảnh chụp từ m&aacute;y ảnh kỹ thuật số th&ocirc;i th&igrave; Kodak Picture CD l&agrave; kh&ocirc;ng cần thiết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; 4 lưu &yacute; để chọn mua đầu DVD chất lượng tốt nhất. Hi vọng b&agrave;i viết đ&atilde; mang lại những th&ocirc;ng tin hữu &iacute;ch cho bạn, gi&uacute;p bạn chọn mua được chiếc đầu DVD ph&ugrave; hợp v&agrave; ưng &yacute; nhất.&nbsp;</p>', 1, '2017-01-23 11:17:50', 65, NULL, '', NULL, NULL, NULL, NULL),
(6, 'Tuyển dụng tháng 1/2017', 'thongbao_58863b196c6d9.jpg', '<p>Tuyển dụng th&aacute;ng 1/2017</p>', '<p>Tuyển dụng th&aacute;ng 1/2017</p>', 0, '2017-01-23 11:19:21', 66, NULL, '', NULL, NULL, NULL, NULL),
(7, 'Điểm danh những món đồ gia dụng không thể thiếu trong ngày Tết', 'popbanner_tet2017_58863b6350672.jpg', '<p>Trang tr&iacute; nh&agrave; cửa dịp cuối năm l&agrave; việc m&agrave; hầunhư tất cả mọi gia đ&igrave;nh Việt Nam đều l&agrave;m để chuẩn bị đ&oacute;n Tết. Ngo&agrave;i việc dọn đẹphay sơn sửa lại ng&ocirc;i nh&agrave; th&igrave; lựa chọn những đồ vật trang tr&iacute;, mua sắm th&ecirc;m đồd&ugrave;ng cũng l&agrave; một phần kh&ocirc;ng thể thiếu để mang th&ecirc;m sắc xu&acirc;n v&agrave;o mỗi ng&ocirc;i nh&agrave;.</p>', '<p>Ng&agrave;y xưa, cứ th&aacute;ng chạp đến l&agrave; c&aacute;c b&agrave; c&aacute;c mẹ bắt đầu lại lục tục chuẩn bị nhữngđồ d&ugrave;ng nh&agrave; bếp để sẵn s&agrave;ng cho những m&oacute;n Tết. Cối xay đ&aacute; để gi&atilde; th&oacute;c nếp đượck&eacute;o ra đề ch&ugrave;i rửa, c&aacute;c nồi to để nấu b&aacute;nh chưng b&aacute;nh T&eacute;t tr&ecirc;n g&aacute;c được l&ocirc;i xuốngđể vệ sinh&hellip;&nbsp;</p>\r\n\r\n<p>Tết ng&agrave;y nay, những vật dụng từng quen thuộc như cối xay, nồi cỡ đại, đống củi...dần được thay thế bằng những sản phẩm gia dụng hiện đại để c&ocirc;ng việc nội trợ trởn&ecirc;n nhẹ nh&agrave;ng hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/popbanner_tet2017.jpg" style="height:400px; width:600px" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n gi&ograve; nấu đ&ocirc;ng, canh xương hầm măng, hay một con g&agrave; luộc để c&uacute;ng gia ti&ecirc;n đ&oacute;l&agrave; những m&oacute;n ăn kh&ocirc;ng thể thiếu trong ng&agrave;y Tết. Tiết kiệm gas hơn, chế biếnnhanh hơn v&agrave; giữ trọn được m&ugrave;i vị, vitamin của m&oacute;n ăn đ&oacute; l&agrave; những g&igrave; m&agrave; một chiếcnồi &aacute;p suất mang lại. Để phục vụ nhu cầu của c&aacute;c gia đ&igrave;nh trong dịp Tết Trầnanh đ&atilde; đưa gia nhiều mức gi&aacute; si&ecirc;u ưu đ&atilde;i c&ugrave;ng với nhiều phần qu&agrave; hấp dẫn như nồi&aacute;p suất điện của Kangaroo giảm gi&aacute; đến 41% chỉ c&ograve;n 1,159 triệu, hay nồi &aacute;p suấtga của Sunhouse giảm từ 850 ngh&igrave;n xuống c&ograve;n 499 ngh&igrave;n .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lẩu, đồ nướng l&agrave; bữa tiệc thể thiếu trong ng&agrave;y Tết sau khi thưởng thức nhữngm&oacute;n ăn truyền thống v&igrave; thế kh&ocirc;ng c&oacute; l&yacute; do g&igrave; gia đ&igrave;nh bạn lại kh&ocirc;ng sở hữu bếptừ, nồi lẩu, bếp nướng trong nh&agrave;. Tiệc t&ugrave;ng c&oacute; thể l&agrave;m bạn cảm gi&aacute;c ng&aacute;n ngấytrong ng&agrave;y Tết th&igrave; những m&oacute;n như nước &eacute;p hay sinh tố rất cần thiết để bổ sungv&agrave;o thực đơn của gia đ&igrave;nh bạn, kh&ocirc;ng phải tốn k&eacute;m ra qu&aacute;n m&agrave; bạn c&oacute; thể tự tayl&agrave;m một c&aacute;ch đơn giản với m&aacute;y say sinh tố. Nếu bạn đang băn khoăn kh&ocirc;ng biếtlưa chọn sản phẩm của h&atilde;ng n&agrave;o th&igrave; h&atilde;y để Trần Anh tư vấn cho bạn. Khi mua Bếpđiện từ midea mi chỉ c&ograve;n 499 ngh&igrave;n, bếp từ của Sunhouse c&ograve;n 699 ngh&igrave;n v&agrave; được tặngngay bộ nồi inox cao cấp SH888. M&aacute;y say sinh tố Electrolux giảm 38% c&ograve;n 549k,m&aacute;y xay đa năng giảm 55% c&ograve;n 399k.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/1_04.jpg" style="height:400px; width:600px" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y tết việc sử dụng thực phẩm phục vụ gia đ&igrave;nh, tiếp đ&oacute;n họ h&agrave;ng, bạn b&egrave; tăngcao n&ecirc;n hầu như chiếc tủ lạnh b&eacute; nhỏ ở nh&agrave; trở n&ecirc;n qu&aacute; tải v&agrave; kh&ocirc;ng thể đ&aacute;p ứngđủ nhu cầu mua sắm của c&aacute;c b&agrave; nội trợ khi cần t&iacute;ch trữ đồ ăn. Do đ&oacute; việc muath&ecirc;m tủ lạnh mới c&oacute; dung t&iacute;ch lớn hơn cũng lu&ocirc;n được nhiều gia đ&igrave;nh lựa chọn. Mặtkh&aacute;c tủ lạnh mới khả năng vận h&agrave;nh sẽ tốt hơn đảm bảo giữ cho thực phẩm tươingon trong những ng&agrave;y nghỉ Tết.</p>\r\n\r\n<p>Hiện tại ở Trần anh đang c&oacute; rất nhiều chương tr&igrave;nh khuyến m&atilde;i tủ lạnh phục vụnhu cầu ng&agrave;y Tết của c&aacute;c gia đ&igrave;nh như tủ lạnh của Electrolux 230 l&iacute;t gi&aacute; giảm từ5.740 xuống 5.390 được tặng k&egrave;m ngay 1 &aacute;o sơ mi thương hiệu Việt Tiến. Tủ lạnhPanasonic giảm đến 25% c&ograve;n 3,999 triệu được tặng ngay bộ nồi inox 3 đ&aacute;ySunhouse (16, 18, 24cm) trị gi&aacute; 500.000Đ. Tủ lạnh Samsung với sức chưa 539 l giảm4,910 triệu c&ograve;n 16,990 triệu được tặng th&ecirc;m 1 bộ chăn ga gối thương hiệuEveron.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đừng qu&aacute; mải m&ecirc; chăm s&oacute;c cho căn bếp m&agrave; qu&ecirc;n đi kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch c&aacute;c bạnnh&eacute;. Một chiếc tivi mới v&agrave; 1 bộ d&agrave;n &acirc;m thanh, karaoke sẽ l&agrave;m cho kh&ocirc;ng kh&iacute; ng&agrave;yTết của gia đ&igrave;nh bạn trở n&ecirc;n tưng bừng, rộn r&atilde; hơn bao giờ hết. Thay v&igrave; sử dụngdịch vụ Karaoke ng&agrave;y tết vừa đắt đỏ vừa kh&oacute; đặt ph&ograve;ng th&igrave; đầu tư d&agrave;n Karaoke vừal&agrave; phương &aacute;n tiện lợi, thoải m&aacute;i thể hiện lời ca tiếng h&aacute;t g&oacute;p phần gắn kếtth&ecirc;m t&igrave;nh cảm giữa c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh lại c&oacute; thể phục vụ kh&aacute;ch đếnchơi. Vừa mua h&agrave;ng chất lượng vừa c&oacute; cơ hội tr&uacute;ng thưởng 1 tỉ đồng c&ugrave;ng 6 tiviSUHD c&ugrave;ng v&ocirc; v&agrave;n phần qu&agrave; gi&aacute; trị kh&aacute;c khi bạn gh&eacute; thăm gian h&agrave;ng Samsung tại hệthống si&ecirc;u thị điện m&aacute;y Trần anh. LG cũng đưa ra v&ocirc; v&agrave;n phần qu&agrave; hấp dẫn như tặngbộ nồi đ&aacute;, hộp thủy tinh Lock and Lock v&agrave; gi&aacute; giảm cực s&acirc;u giảm đến 16 triệu đồngkhi mua tivi Oled 55 inch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/banner_samsung.jpg" style="height:400px; width:600px" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y Tết s&uacute;ng s&iacute;nh quần &aacute;o mới, thăm họ h&agrave;ng gần xa, tụ tập bạn b&egrave; chắc chắn bạnđừng qu&ecirc;n ghi lại những khoảng khắc tuyệt vời đ&oacute; bằng smartphone v&agrave; những chiếcm&aacute;y ảnh sắc n&eacute;t nh&eacute;. Nếu chưa sắm th&igrave; nhanh tay l&ecirc;n bạn nh&eacute; v&igrave; Trần anh sắp hếtkhuyến m&atilde;i rồi đ&oacute;. Điện thoại Samsung j510 gi&aacute; cực sốc chỉ c&ograve;n 3.999 triệutrong khi gi&aacute; ni&ecirc;m yết l&agrave; 4,999 triệu m&agrave; c&ograve;n được tặng th&ecirc;m phụ kiện, điện thoạiSamsung j320 gi&aacute; khuyến m&atilde;i c&ograve;n 3,290 được tặng th&ecirc;m mũ bảo hiểm, thẻ nhớ 8gb,thẻ giảm gi&aacute; khi mua phụ kiện v&agrave; được tặng 1 phần qu&agrave; ĐẶC BIỆT nữa.</p>\r\n\r\n<p>T&iacute;n đồ của Iphone chắc chắn sẽ kh&ocirc;ng thể l&agrave;m ngơ trước si&ecirc;u phẩm mới của Iphoneđ&oacute; l&agrave; Iphone 7 với gi&aacute; chỉ c&ograve;n 17,990 triệu được tặng th&ecirc;m miếng d&aacute;n cường lựcv&agrave; giảm 500k khi mua sạc dự ph&ograve;ng. Gioi trẻ đang ph&aacute;t sốt v&igrave; MIC bluetooth tiệndụng v&agrave; bạn sẽ kh&ocirc;ng phải bỏ tiền ra mua n&oacute; khi mua Oppo F1s được tặng k&egrave;mtrong g&oacute;i qu&agrave; trị gi&aacute; 1,1 triệu đồng từ Oppo bao gồm Mic, thẻ nhớ 16gb, gậy tựsướng, v&agrave; phiếu giảm gi&aacute; 500k khi mua sạc dự ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/tbs_04.jpg" style="height:200px; width:600px" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/tbs_05.jpg" style="height:200px; width:600px" />&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến với Trần anh bạn sẽ được tư vấn bởi 1 đội ngũ tư vấn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave;mạng lưới rộng khắp c&aacute;c tỉnh th&agrave;nh gi&uacute;p bạn thuận tiện trong mua sắm, m&agrave; lại tiếtkiệm chi ph&iacute; v&agrave; nhận được nhiều phần qu&agrave; hấp dẫn trong chương tr&igrave;nh &quot; ĐếnTrần anh vui đ&oacute;n Tết&quot;.</p>', 1, '2017-01-23 11:20:35', 63, NULL, '', NULL, NULL, NULL, NULL),
(8, 'Những tính năng trên Galaxy A5 2017 nổi bật được đánh giá cao', '2_58863bc1d5cb9.jpeg', '<p>Galaxy A5 2017 c&oacute; rất nhiều t&iacute;nh năng cao cấp</p>\r\n\r\n<p>C&ugrave;ng Điện m&aacute;y Trần Anh điểm danh những&nbsp;<strong>t&iacute;nh năng tr&ecirc;n Galaxy A5 2017</strong>, được đ&aacute;nh gi&aacute; kh&aacute; cao từ cộng đồng y&ecirc;u c&ocirc;ng nghệ.</p>', '<p><strong>Chống bụi, nước IP68 đỉnh cao</strong></p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/13/2.jpeg" />&nbsp;</p>\r\n\r\n<p>Chống nước cao cấp IP68</p>\r\n\r\n<p>Galaxy A5 2017 c&oacute; t&iacute;nh năng chống nước đỉnh cao, với ti&ecirc;u chuẩn IP68, cho ph&eacute;p người d&ugrave;ng c&oacute; thể ng&acirc;m model n&agrave;y dưới nước ở độ s&acirc;u 1.5 m&eacute;t trong 30 ph&uacute;t li&ecirc;n tục. Điều n&agrave;y gi&uacute;p cho người d&ugrave;ng ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m nếu chẳng may đ&aacute;nh rơi m&aacute;y xuống nước, đi mưa, hay chụp ảnh selfie dưới bể bơi&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến v&acirc;n tay cao cấp</strong></p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/13/3.jpeg" />&nbsp;</p>\r\n\r\n<p>Cảm biến v&acirc;n tay được t&iacute;ch hợp tr&ecirc;n n&uacute;t Home</p>\r\n\r\n<p>Hiện nay, smartphone bảo mật cao lu&ocirc;n được ưu chuộng, ch&iacute;nh v&igrave; vậy Galaxy A5 2017 cũng được trang bị cảm biến v&acirc;n tay nhằm thu h&uacute;t người d&ugrave;ng hơn. Cảm biến v&acirc;n tay tr&ecirc;n thiết bị n&agrave;y được đặt ở n&uacute;t Home, tạo cảm gi&aacute;c rất sang trọng v&agrave; nổi bật kh&ocirc;ng kh&aacute;c c&aacute;c sản phẩm cao cấp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sạc nhanh ấn tượng</strong></p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/13/4.jpeg" />&nbsp;</p>\r\n\r\n<p>A5 2017 cũng c&oacute; hỗ trợ sạc nhanh giống như tr&ecirc;n Galaxy S7</p>\r\n\r\n<p>T&iacute;nh năng sạc nhanh cũng được trang bị tr&ecirc;n Galaxy A5 2017, điều n&agrave;y gi&uacute;p cho người d&ugrave;ng gạt đi nỗi lo hết pin trong những trường hợp khẩn cấp. Chắc chắn đ&acirc;y sẽ l&agrave; một trong những t&iacute;nh năng được nhiều người d&ugrave;ng ch&uacute; &yacute;, bởi hiện nay nhiều người d&ugrave;ng lu&ocirc;n mong muốn c&oacute; được một sản phẩm c&oacute; thời gian sạc nhanh v&agrave; sử dụng trong thời gian d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&oacute; t&iacute;nh năng Always On Display</strong></p>\r\n\r\n<p>Always On Display l&agrave; t&iacute;nh năng cho ph&eacute;p hiển thị c&aacute;c th&ocirc;ng b&aacute;o về ng&agrave;y giờ, cuộc gọi, tinh nhắn&hellip; kể cả khi m&agrave;n h&igrave;nh điện thoại đ&atilde; tắt. Đ&acirc;y l&agrave; một t&iacute;nh năng v&ocirc; c&ugrave;ng th&uacute; vị, từng được Samsung trang bị tr&ecirc;n Galaxy S7 v&agrave; Galaxy S7 Edge.</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2017/01/13/5.jpeg" />&nbsp;</p>\r\n\r\n<p>Always On Display l&agrave; t&iacute;nh năng mới kh&aacute; th&uacute; vị</p>\r\n\r\n<p>Việc Galaxy A5 2017 c&oacute; t&iacute;nh năng n&agrave;y khiến cho m&aacute;y c&agrave;ng trở n&ecirc;n thu h&uacute;t người d&ugrave;ng, bởi model n&agrave;y chỉ l&agrave; một sản phẩm tầm trung nhưng lại c&oacute; trang bị cao cấp giống hệt chiếc Galaxy S7. Nhiều người c&ograve;n nhận định, Galaxy A5 2017 ch&iacute;nh l&agrave; bản thu gọn của Galaxy S7.</p>\r\n\r\n<p>Hiện tại, A5 2017 chưa c&oacute; th&ocirc;ng tin ch&iacute;nh thức thời điểm b&aacute;n ra ch&iacute;nh h&atilde;ng tại Việt Nam. Tuy nhi&ecirc;n, dựa tr&ecirc;n những&nbsp;<strong>t&iacute;nh năng tr&ecirc;n Galaxy A5 2017</strong>, c&oacute; thể khẳng định model n&agrave;y sẽ cực kỳ thu h&uacute;t nếu b&aacute;n ra thị trường nước ta.</p>', 1, '2017-01-23 11:22:09', 63, NULL, '', NULL, NULL, NULL, NULL),
(9, 'So găng 3 "ông lớn" bán lẻ điện máy', 'dsc_1290_sua_58863c3d63c51.jpg', '<p>Theo b&aacute;o c&aacute;o mới nhất về ng&agrave;nh ph&acirc;n phối b&aacute;n lẻ của C&ocirc;ng ty chứng kho&aacute;n MB (MBS), Trần Anh, Điện M&aacute;y Xanh, Nguyễn Kim đang l&agrave; 3 doanh nghiệp điện m&aacute;y khai th&aacute;c mặt bằng kinh doanh hiệu quả nhất trong lĩnh vực b&aacute;n lẻ điện m&aacute;y.</p>', '<p>Điều n&agrave;y kh&ocirc;ng qu&aacute; kh&oacute; hiểu bởi hiện nay 3 &ocirc;ng lớn n&agrave;y đang được coi l&agrave; &ldquo;tam trụ&rdquo; thống lĩnh thị trường điện m&aacute;y trong nước.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/09/26/dsc_1290_sua.jpg" />&nbsp;</p>\r\n\r\n<p><br />\r\nNổi đ&igrave;nh nổi đ&aacute;m nhất hiện nay kh&ocirc;ng thể kh&ocirc;ng nhắc tới Điện m&aacute;y Xanh với hơn 100 si&ecirc;u thị trải khắp 63 tỉnh th&agrave;nh tr&ecirc;n cả nước. Đi k&egrave;m với đ&oacute;, những th&ocirc;ng tin c&ocirc;ng bố về mức doanh thu v&agrave; lợi nhuận của chuỗi n&agrave;y cũng khiến kh&ocirc;ng &iacute;t doanh nghiệp tr&ograve;n mắt v&agrave; th&egrave;m muốn. Kh&ocirc;ng chỉ g&acirc;y ấn tượng về tốc độ ph&aacute;t triển, hướng đi mới mẻ của Điện m&aacute;y Xanh cũng được nhiều chuy&ecirc;n gia đ&aacute;nh gi&aacute; cao.<br />\r\n<br />\r\nTrong khi Điện m&aacute;y Xanh b&ugrave;ng nổ tr&ecirc;n ph&acirc;n kh&uacute;c si&ecirc;u thị quy m&ocirc; nhỏ th&igrave; Điện m&aacute;y Trần Anh lại &quot;một m&igrave;nh một chợ&quot; chiếm lĩnh ph&acirc;n kh&uacute;c si&ecirc;u thị quy m&ocirc; lớn đi k&egrave;m với c&aacute;c dịch vụ tiện &iacute;ch. Dự t&iacute;nh đến cuối năm 2016, Trần Anh sẽ sở hữu hơn 39 đại si&ecirc;u thị trải d&agrave;i từ Bắc đến Trung v&agrave; ch&iacute;nh thức trở th&agrave;nh si&ecirc;u thị điện m&aacute;y miền Bắc đầu ti&ecirc;n c&oacute; mặt tại th&agrave;nh phố Đ&agrave; Nẵng với 2 đại si&ecirc;u thị điện m&aacute;y quy m&ocirc; ho&agrave;nh tr&aacute;ng. Tham vọng của Trần Anh trong 5 năm tới l&agrave; sẽ sở hữu hơn 100 si&ecirc;u thị phủ khắp thị trường Việt Nam v&agrave; đat doanh thu 1 tỉ USD.<br />\r\n<br />\r\nVề phần Nguyễn Kim, doanh nghiệp n&agrave;y đ&atilde; từng ở tr&ecirc;n đỉnh cao vinh quang khi được coi l&agrave; &ocirc;ng lớn tại thị trường ph&iacute;a Nam v&agrave; trở th&agrave;nh doanh nghiệp điện m&aacute;y ph&iacute;a Nam Bắc tiến th&agrave;nh c&ocirc;ng. Trong số 3 đại gia lớn nhất, Nguyễn Kim hiện chỉ sở hữu 21 trung t&acirc;m tại ph&iacute;a Nam, Đ&agrave; Nẵng v&agrave; H&agrave; Nội.<br />\r\n<br />\r\n<strong>Cuộc cạnh tranh đầy khốc liệt</strong><br />\r\n<br />\r\nTheo b&aacute;o c&aacute;o ng&agrave;nh ph&acirc;n phối b&aacute;n lẻ của MBS, Trần Anh đang l&agrave; chuỗi b&aacute;n lẻ điện m&aacute;y khai th&aacute;c mặt bằng kinh doanh hiệu quả nhất, với doanh thu trung b&igrave;nh 20,3 triệu đồng/m2/th&aacute;ng.<br />\r\n<br />\r\nTheo ph&acirc;n t&iacute;ch của c&aacute;c chuy&ecirc;n gia, c&aacute;c điểm b&aacute;n của Trần Anh c&oacute; sự kh&aacute;c biệt rất lớn so với c&aacute;c doanh nghiệp kh&aacute;c.<br />\r\n<br />\r\nĐầu ti&ecirc;n phải kể đến đ&oacute; ch&iacute;nh l&agrave; c&aacute;c trung t&acirc;m của Trần Anh đều nằm tại c&aacute;c &ldquo;điểm v&agrave;ng&rdquo; tại c&aacute;c tỉnh th&agrave;nh. Điều n&agrave;y quyết định rất lớn tới hiệu quả kinh doanh của Trần Anh, bởi trong b&aacute;n lẻ yếu tố quan trọng nhất đ&oacute; l&agrave; mặt bằng. Trong khi c&aacute;c địa điểm to, đẹp tại c&aacute;c vị tr&iacute; đắc địa kh&ocirc;ng phải nhiều, nếu kh&ocirc;ng nhanh ch&acirc;n sẽ rất dễ bị c&aacute;c doanh nghiệp kh&aacute;c lấy mất. Do vậy, chỉ trong v&ograve;ng 2 năm trở lại đ&acirc;y, Trần Anh đ&atilde; dồn to&agrave;n lực đẩy nhanh tốc độ mở đại si&ecirc;u thị tại c&aacute;c tỉnh, th&agrave;nh để chiếm được mặt bằng tại c&aacute;c khu phố ch&iacute;nh.<br />\r\n<br />\r\nNgo&agrave;i việc t&acirc;p trung ph&aacute;t triển c&aacute;c si&ecirc;u thị với diện t&iacute;ch lớn để trưng b&agrave;y sản phẩm gi&uacute;p kh&aacute;ch h&agrave;ng thỏa sức lựa chọn v&agrave; trải nghiệm, Trần Anh c&ograve;n t&iacute;ch hợp th&ecirc;m c&aacute;c dịch vụ kh&ocirc;ng ở đ&acirc;u c&oacute;. Điểm nhấn đặc biệt trong đ&oacute; ch&iacute;nh l&agrave; khu vui chơi d&agrave;nh cho trẻ em v&agrave; b&atilde;i đỗ xe rộng r&atilde;i phục vụ ho&agrave;n hảo cho cả c&aacute;c kh&aacute;ch h&agrave;ng đầy tiềm năng l&agrave; những đối tượng đi &ocirc; t&ocirc;.<br />\r\n<br />\r\nTheo như đại diện doanh nghiệp n&agrave;y từng ph&aacute;t biểu, Trần Anh chấp nhận bỏ chi ph&iacute; lớn khi mở quy m&ocirc; đại si&ecirc;u thị để đạt được mục ti&ecirc;u tạo sự kh&aacute;c biệt trong chất lượng dịch vụ kh&aacute;ch h&agrave;ng. Đ&acirc;y được coi l&agrave; nước cờ kh&aacute; kh&ocirc;n ngoan, thể hiện được tầm nh&igrave;n xa tr&ocirc;ng rộng khi quyết định hi sinh c&aacute;i lợi nhỏ trước mắt để thu về lợi &iacute;ch lớn về l&acirc;u d&agrave;i bởi ch&iacute;nh yếu tố dịch vụ v&agrave; kh&ocirc;ng gian mua sắm mới l&agrave; điều thu h&uacute;t v&agrave; n&iacute;u giữ kh&aacute;ch h&agrave;ng đến với si&ecirc;u thị.<br />\r\n<br />\r\nKh&ocirc;ng chỉ c&oacute; vậy, Trần Anh c&ograve;n đang khai th&aacute;c rất tốt những lợi thế ri&ecirc;ng c&oacute; của m&igrave;nh đ&oacute; l&agrave; đối t&aacute;c Nhật Bản hỗ trợ c&ugrave;ng mục ti&ecirc;u hướng tới chất lượng Nhật Bản&rdquo;. Theo &ocirc;ng Ng&ocirc; Th&agrave;nh Đạt, gi&aacute;m đốc Marketing của Trần Anh th&igrave; những điều họ truyền tải tới kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chỉ l&agrave; khẩu hiệu cho vui m&agrave; đi theo đ&oacute; l&agrave; cả một loạt sự thay đổi về cơ cấu tổ chức, ch&iacute;nh s&aacute;ch, dịch vụ&hellip; &quot;để l&agrave;m sao khi đặt ch&acirc;n tới Trần Anh kh&aacute;ch h&agrave;ng đều cảm nhận được chất Nhật Bản trong đ&oacute;&quot;.<br />\r\n<br />\r\nKh&ocirc;ng ngừng mở rộng quy m&ocirc;, khai th&aacute;c điểm b&aacute;n cực kỳ hiệu quả, c&ugrave;ng tham vọng kh&ocirc;ng nhỏ v&agrave; chiến lược marketing kh&ocirc;n ngoan, Trần Anh xứng đ&aacute;ng trở th&agrave;nh đối thủ m&agrave; bất kỳ doanh nghiệp n&agrave;o cũng phải e ngại.<br />\r\n<br />\r\nĐứng ở vị tr&iacute; thứ 2, Nguyễn Kim cũng đang được đ&aacute;nh gi&aacute; cao về hiệu quả kinh doanh của từng điểm b&aacute;n với 18 triệu/m2. Tuy nhi&ecirc;n, kh&ocirc;ng giống như Trần Anh, từ sau khi &ldquo;b&aacute;n m&igrave;nh&rdquo; cho đại gia Central Group, Nguyễn Kim dường như nằm ngo&agrave;i cuộc đua s&ocirc;i động tr&ecirc;n thị trường điện m&aacute;y khi kh&ocirc;ng c&oacute; bất kỳ một động th&aacute;i n&agrave;o mới mẻ trong hoạt động kinh doanh, kể cả mở rộng quy m&ocirc; như đ&atilde; tuy&ecirc;n bố.<br />\r\n<br />\r\nD&ugrave; thời gian gần đ&acirc;y, th&ocirc;ng tin Central Group &ldquo;đuổi&rdquo; Thế giới di động ra khỏi Big C được giới trong ng&agrave;nh xem như một động th&aacute;i dẫn đường cho sự trở lại của Nguyễn Kim, nhưng hướng đi của &ocirc;ng lớn n&agrave;y vẫn chưa cho thấy điều g&igrave; đ&oacute; r&otilde; r&agrave;ng để c&oacute; thể tạo ra sự kh&aacute;c biệt so với 2 đối thủ c&ograve;n lại l&agrave; Trần Anh v&agrave; Điện m&aacute;y Xanh. Điều n&agrave;y thực sự khiến kh&ocirc;ng &iacute;t người lo ngại rằng khi về tay người Th&aacute;i, Nguyễn Kim c&oacute; thể đang bị &ldquo;khớp&rdquo; v&agrave; chưa t&igrave;m được tiếng n&oacute;i chung trong chiến lược ph&aacute;t triển của m&igrave;nh.<br />\r\n<br />\r\nĐiện m&aacute;y Xanh gia nhập sau v&agrave;o thị trường b&aacute;n lẻ điện m&aacute;y theo m&ocirc; h&igrave;nh chuỗi si&ecirc;u thị nhỏ v&igrave; vậy mức hiệu quả khai th&aacute;c ở mức thấp khoảng 12 triệu đồng/m2/th&aacute;ng. Ra đời từ năm 2010 nhưng phải đến năm 2015, c&aacute;i t&ecirc;n điện m&aacute;y xanh mới trở th&agrave;nh hiện tượng g&acirc;y kinh ngạc cho thị trường điện m&aacute;y khi nhanh ch&oacute;ng phủ khắp 63 tỉnh th&agrave;nh của Việt Nam. Nhanh ch&oacute;ng v&agrave; quyết liệt, kh&ocirc;ng qu&aacute; kh&oacute; hiểu khi doanh nghiệp n&agrave;y đang đứng ở vị tr&iacute; số 3 về việc khai th&aacute;c mặt bằng b&aacute;n lẻ<br />\r\n<br />\r\nL&yacute; do để chuỗi điện m&aacute;y n&agrave;y vươn l&ecirc;n trở th&agrave;nh Top 3 nh&agrave; b&aacute;n lẻ khai th&aacute;c tốt mặt bằng đ&oacute; ch&iacute;nh l&agrave; nhờ hướng mở si&ecirc;u thị diện t&iacute;ch nhỏ để dễ d&agrave;ng tiếp cận với c&aacute;c đối tượng kh&aacute;ch h&agrave;ng m&agrave; đối thủ chưa với tới được.<br />\r\n<br />\r\nC&aacute;c chuy&ecirc;n gia cũng mổ xẻ những bất lợi của điện m&aacute;y Xanh khi với quy m&ocirc; nhỏ, c&aacute;c trung t&acirc;m n&agrave;y rất kh&oacute; để đa dạng h&agrave;ng h&oacute;a v&agrave; tạo ra kh&ocirc;ng gian mua sắm tiện nghi cho kh&aacute;ch h&agrave;ng, trong khi với mức thu nhập người d&acirc;n ng&agrave;y c&agrave;ng tăng l&ecirc;n th&igrave; người ti&ecirc;u d&ugrave;ng ng&agrave;y c&agrave;ng muốn lựa chọn c&aacute;c sản phẩm đắt tiền hơn với những c&ocirc;ng năng nhiều hơn, v&agrave; đương nhi&ecirc;n diện t&iacute;ch trưng b&agrave;y cũng cần lớn hơn. Chưa kể l&agrave; với quy m&ocirc; nhỏ, th&igrave; những đối tượng kh&aacute;ch h&agrave;ng lớn đi &ocirc; t&ocirc; lại kh&aacute; kh&oacute; khăn để tiếp cận được<br />\r\n<br />\r\nĐ&oacute; ch&iacute;nh l&agrave; l&yacute; do khiến Điện m&aacute;y Xanh d&ugrave; sở hữu nhiều điểm b&aacute;n hơn Trần Anh v&agrave; Nguyễn Kim nhưng lại kh&ocirc;ng theo kịp 2 đại gia n&agrave;y về việc khai th&aacute;c hiệu quả tr&ecirc;n từng điểm b&aacute;n<br />\r\n<br />\r\nMỗi doanh nghiệp đều đang sở hữu những lợi thế cũng như đ&ograve;n v&otilde; của ri&ecirc;ng m&igrave;nh. Với tiềm lực t&agrave;i ch&iacute;nh h&ugrave;ng hậu, kinh nghiệm quản trị c&ugrave;ng với tham vọng &hellip; cả 3 đang tạo ra một cuộc đối đầu ngấm ngầm nhưng kh&ocirc;ng k&eacute;m phần khốc liệt.</p>', 2, '2017-01-23 11:24:13', 63, NULL, '', NULL, NULL, NULL, NULL),
(10, 'Tuyển dụng nhân viên kinh doanh tháng 1/2017', 'thongbao_58863c85021d2.jpg', '<p>Tuyển dụng nh&acirc;n vi&ecirc;n kinh doanh th&aacute;ng 1/2017</p>', '<p>Tuyển dụng nh&acirc;n vi&ecirc;n kinh doanh th&aacute;ng 1/2017</p>', 0, '2017-01-23 11:25:25', 66, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `posts` (`id`, `title`, `image`, `info`, `content`, `status`, `date_created`, `cate_id`, `facebook`, `video`, `seo_link`, `seo_title`, `seo_keyword`, `seo_description`) VALUES
(11, 'Thế đối trọng trên thị trường điện máy', '1107_the_doi_trong_tren_thi_truong_dien_may_58863ccb68c43.jpg', '<p>Kh&aacute;ch h&agrave;ng của c&aacute;c si&ecirc;u thị điện m&aacute;y ng&agrave;y c&agrave;ng quan t&acirc;m tới chất lượng dịch vụ v&agrave; những tiện &iacute;ch đi k&egrave;m<br />\r\n&nbsp;</p>', '<p><strong>Xu hướng co nhỏ diện t&iacute;ch</strong><br />\r\n<br />\r\nNăm 2016, thị trường b&aacute;n lẻ Việt Nam c&oacute; thể đạt doanh số 100 tỷ USD, trong đ&oacute; ng&agrave;nh h&agrave;ng điện tử, điện m&aacute;y khoảng 10 tỷ USD. Thực tế, tr&ecirc;n thị trường điện m&aacute;y đang diễn ra cuộc cạnh tranh khốc liệt với hai xu hướng tr&aacute;i ngược. Trong khi, một số DN đi theo hướng đại si&ecirc;u thị, c&oacute; DN lại chỉ mở những si&ecirc;u thị mini.<br />\r\n<br />\r\nĐầu tư theo hướng co nhỏ diện t&iacute;ch, c&aacute;c si&ecirc;u thị điện m&aacute;y được x&acirc;y dựng với quy m&ocirc; nhỏ, kh&ocirc;ng đầu tư nhiều cho hạ tầng lẫn dịch vụ đi k&egrave;m, chi ph&iacute; đầu tư cũng rất khi&ecirc;m tốn. Đại diện cho xu thế n&agrave;y, phải kể đến hệ thống Điện M&aacute;y Xanh.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/07/11/1107_the_doi_trong_tren_thi_truong_dien_may.jpg" /></p>\r\n\r\n<p><br />\r\nTheo đ&oacute;, những năm gần đ&acirc;y thương hiệu n&agrave;y c&oacute; tốc độ ph&aacute;t triển nhanh ch&oacute;ng mặt, vượt qua nhiều t&ecirc;n tuổi như Nguyễn Kim, Chợ Lớn, Thi&ecirc;n H&ograve;a&hellip; Nguy&ecirc;n nh&acirc;n đầu ti&ecirc;n gi&uacute;p Điện M&aacute;y Xanh c&oacute; thể nhanh ch&oacute;ng bứt ph&aacute;, nhờ việc ph&aacute;t triển hệ thống si&ecirc;u thị quy m&ocirc; nhỏ, từ đ&oacute; dễ d&agrave;ng mở rộng mạng lưới, đồng thời tiết giảm tối ưu chi ph&iacute;.<br />\r\n<br />\r\nTheo đ&oacute;, trung b&igrave;nh si&ecirc;u thị của Điện M&aacute;y Xanh c&oacute; diện t&iacute;ch từ 800 - 1.000m2, trong khi diện t&iacute;ch chuẩn của một si&ecirc;u thị điện m&aacute;y 4.000 - 5.000m2&hellip; B&ugrave; lại, ph&aacute;t triển theo xu hướng n&agrave;y gi&uacute;p cho Điện M&aacute;y Xanh dễ d&agrave;ng c&oacute; mặt tại nhiều điểm tr&ecirc;n địa b&agrave;n cả nước. Hiện, DN n&agrave;y đang sở hữu hơn 100 si&ecirc;u thị, v&agrave; hết năm 2016 sẽ c&oacute; khoảng 150 si&ecirc;u thị. Địa b&agrave;n của hệ thống si&ecirc;u thị mini n&agrave;y chủ yếu tập trung ở khu vực &ldquo;đ&ocirc; thị cấp 2&rdquo;, trải rộng ở nhiều địa phương trong cả nước.<br />\r\n<br />\r\nTừ những th&agrave;nh c&ocirc;ng bước đầu của Điện M&aacute;y Xanh, xu hướng co nhỏ diện t&iacute;ch đ&atilde; bắt đầu lan sang một số thương hiệu kh&aacute;c. Trong đ&oacute;, c&oacute; thể kể đến c&aacute;c cửa h&agrave;ng của Media Mart cũng chỉ c&oacute; diện t&iacute;ch từ 700 - 1.500 m2.<br />\r\n<br />\r\nTại khu vực ph&iacute;a Nam c&ograve;n c&oacute; thương hiệu Điện m&aacute;y Thi&ecirc;n H&ograve;a cũng đang chạy thử nghiệm m&ocirc; h&igrave;nh cửa h&agrave;ng nhỏ. Dự kiến, trong năm 2017 Thi&ecirc;n H&ograve;a sẽ nh&acirc;n rộng ra khoảng 40 cửa h&agrave;ng&hellip;<br />\r\n<br />\r\nTuy ph&aacute;t triển nhanh số lượng, song xu hướng n&agrave;y đ&atilde; bộc lộ nhiều điểm bất lợi. Bất lợi đầu ti&ecirc;n, do diện t&iacute;ch nhỏ n&ecirc;n c&aacute;c si&ecirc;u thị kh&ocirc;ng thể đ&aacute;p ứng được những tiện &iacute;ch đi k&egrave;m như khu vui chơi d&agrave;nh cho trẻ em, khu trải nghiệm sản phẩm, b&atilde;i đậu xe&hellip;<br />\r\n<br />\r\nĐặc biệt, do diện t&iacute;ch nhỏ hẹp n&ecirc;n c&aacute;c si&ecirc;u thị nằm trong xu thế n&agrave;y thường chỉ tập trung v&agrave;o những nh&oacute;m sản phẩm chủ lực, h&agrave;ng h&oacute;a &iacute;t đi sự phong ph&uacute;, đa dạng.<br />\r\n<br />\r\n<strong>M&ocirc; h&igrave;nh đại si&ecirc;u thị</strong><br />\r\n<br />\r\nTrong khi, một số chủ đầu tư si&ecirc;u thị điện m&aacute;y t&igrave;m c&aacute;ch co nhỏ diện t&iacute;ch, th&igrave; ở chiều ngược lại c&oacute; những DN đang quyết t&acirc;m x&acirc;y dựng theo hướng m&ocirc; h&igrave;nh đại si&ecirc;u thị. Ti&ecirc;u biểu cho m&ocirc; h&igrave;nh n&agrave;y đang l&agrave; hệ thống si&ecirc;u thị Trần Anh. Thời gian qua, th&ocirc;ng tin Trần Anh c&ocirc;ng bố sẽ li&ecirc;n tiếp mở th&ecirc;m nhiều đại si&ecirc;u thị điện m&aacute;y tại thị trường c&aacute;c tỉnh miền Bắc v&agrave; miền Trung g&acirc;y nhiều bất ngờ cho thị trường.<br />\r\n<br />\r\nSắp tới đ&acirc;y, Trần Anh sẽ tiếp tục lấn v&agrave;o thị trường miền Trung, khi đ&acirc;y l&agrave; thương hiệu điện m&aacute;y miền Bắc đầu ti&ecirc;n c&oacute; mặt tại TP. Đ&agrave; Nẵng. Dự kiến, đến hết năm 2016 Trần Anh sẽ sở hữu 40 đại si&ecirc;u thị điện m&aacute;y tr&ecirc;n địa b&agrave;n cả nước. Đi theo m&ocirc; h&igrave;nh đại si&ecirc;u thị, c&aacute;c trung t&acirc;m mua sắm của Trần Anh c&oacute; diện t&iacute;ch từ 2000m2 đến hơn 5000m2, với nhiều tiện &iacute;ch đi k&egrave;m.<br />\r\n<br />\r\n&Ocirc;ng Ng&ocirc; Th&agrave;nh Đạt, Gi&aacute;m đốc Marketing Hệ thống Điện m&aacute;y - M&aacute;y t&iacute;nh - Điện thoại Trần Anh cho rằng, DN n&agrave;o muốn tồn tại cũng đều phải đặc biệt quan t&acirc;m tới b&agrave;i to&aacute;n kiểm so&aacute;t chi ph&iacute;, tối ưu hiệu quả tr&ecirc;n từng điểm b&aacute;n.<br />\r\n<br />\r\nTuy nhi&ecirc;n, Trần Anh giải b&agrave;i to&aacute;n n&agrave;y kh&ocirc;ng phải bằng c&aacute;ch co nhỏ diện t&iacute;ch mặt bằng đến mức khiến kh&aacute;ch h&agrave;ng cảm thấy b&iacute; b&aacute;ch, kh&ocirc;ng thuận tiện khi mua sắm. Thay v&agrave;o đ&oacute;, họ sẽ tối ưu chi ph&iacute; bằng m&ocirc; h&igrave;nh tổ chức, bằng tối ưu hiệu quả tồn kho, bằng sự h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng v&agrave; ch&iacute;nh s&aacute;ch th&uacute;c đẩy nh&acirc;n vi&ecirc;n&hellip;<br />\r\n<br />\r\nVậy, nguy&ecirc;n nh&acirc;n n&agrave;o khiến Trần Anh hay một số chủ đầu tư kh&aacute;c đi ngược lại với xu thế co nhỏ của c&aacute;c si&ecirc;u thị để dễ d&agrave;ng bứt ph&aacute;, đặc biệt, trong bối cảnh việc huy động t&agrave;i ch&iacute;nh để mở c&aacute;c si&ecirc;u thị lớn kh&ocirc;ng phải l&agrave; chuyện dễ. Theo nhiều chuy&ecirc;n gia th&igrave; hướng đi của Trần Anh kh&ocirc;ng kh&oacute; giải th&iacute;ch. Bởi, chiến lược cạnh tranh của mỗi DN thường dựa tr&ecirc;n thế mạnh của m&igrave;nh.<br />\r\n<br />\r\nTheo đ&oacute;, ưu thế của Trần Anh, so với một số &ldquo;đối thủ&rdquo; kh&aacute;c l&agrave; dựa tr&ecirc;n sức mạnh về vốn, bề d&agrave;y kinh nghiệm quản trị khi kết hợp với thương hiệu điện m&aacute;y Nojima của Nhật Bản. Đ&acirc;y l&agrave; một trong những &ldquo;&ocirc;ng lớn&rdquo; trong hệ thống si&ecirc;u thị điện m&aacute;y ở đất nước mặt trời mọc, khi Nojima c&oacute; 60 năm trong nghề c&ugrave;ng hơn 600 si&ecirc;u thị điện m&aacute;y tại Nhật Bản.<br />\r\n<br />\r\nTrong thực tế, kể từ khi hợp t&aacute;c với Nojima, Trần Anh đ&atilde; c&oacute; những nước đi b&agrave;i bản, nhanh ch&oacute;ng c&oacute; mặt tại những đ&ocirc; thị lớn tại miền Bắc v&agrave; sắp tới đ&acirc;y l&agrave; tại miền Trung. Ph&aacute;t triển c&aacute;c đại si&ecirc;u thị ở những đ&ocirc; thị lớn, c&oacute; thể l&agrave; bước đi kh&ocirc;n ngoan, bởi thực tế những vị tr&iacute; đắc địa x&acirc;y dựng si&ecirc;u thị tại những th&agrave;nh phố lớn kh&ocirc;ng phải nhiều. Sau khi, c&oacute; được những &ldquo;v&iacute; tr&iacute; v&agrave;ng&rdquo;, th&igrave; việc ph&aacute;t triển hệ thống si&ecirc;u thị &ldquo;ch&acirc;n rết&rdquo; sẽ dễ d&agrave;ng hơn nhiều.<br />\r\n<br />\r\nTr&aacute;i ngược với xu thế co nhỏ, si&ecirc;u thị mini&hellip; với m&ocirc; h&igrave;nh đại si&ecirc;u thị c&oacute; diện t&iacute;ch lớn sẽ cung cấp nhiều dịch vụ cũng như tiện &iacute;ch đi k&egrave;m khu vui chơi d&agrave;nh cho trẻ em, khu vực trải nghiệm sản phẩm, b&atilde;i đậu xe rộng r&atilde;i.<br />\r\n<br />\r\nTrong thực tế, hiện kh&aacute;ch h&agrave;ng ng&agrave;y c&agrave;ng quan t&acirc;m tới chất lượng dịch vụ v&agrave; c&aacute;c tiện &iacute;ch đi k&egrave;m. Đặc biệt, ở m&ocirc; h&igrave;nh đại si&ecirc;u thị c&oacute; nhiều kh&ocirc;ng gian để giới thiệu, trưng b&agrave;y sản phẩm h&agrave;ng ho&aacute; hơn...<br />\r\n<br />\r\nViệc th&agrave;nh c&ocirc;ng của xu hướng co nhỏ diện t&iacute;ch, hay sự &ldquo;l&ecirc;n ng&ocirc;i&rdquo; m&ocirc; h&igrave;nh đại si&ecirc;u thị, thời gian sẽ chứng minh. Song, trước mắt sự cạnh tranh của hai xu thế đối lập n&agrave;y đ&atilde; v&agrave; đang tạo n&ecirc;n bức tranh s&ocirc;i động hơn cho thị trường điện m&aacute;y trong nước.</p>\r\n\r\n<p><strong>Theo Thoibaonganhang</strong></p>', 1, '2017-01-23 11:26:35', 63, NULL, '', NULL, NULL, NULL, NULL),
(12, 'Bật mí 5 tiện ích tuyệt vời có sẵn trên Android', '1409758833377_wps_66_Samsung_Note_Edge3_jpg_58863d4864b64.jpg', '<p><strong>C&oacute; thể bạn chưa biết hoặc chưa bao giờ sử dụng những tiện &iacute;ch tuyệt vời m&agrave; Google đ&atilde; t&iacute;ch hợp sẵn tr&ecirc;n hệ điều h&agrave;nh Android trong c&aacute;c d&ograve;ng Smartphone. H&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i t&igrave;m hiểu nh&eacute;.</strong></p>', '<p><strong>Ứng dụng từ Google Chrome với tiện &iacute;ch tiết kiệm dữ liệu.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ứng dụng từ Google Chrome cho ph&eacute;p truy cập v&agrave; xử l&yacute; dữ liệu web nhanh hơn. Ngo&agrave;i ra trong qu&aacute; tr&igrave;nh sử dụng n&oacute; c&ograve;n gi&uacute;p m&igrave;nh giảm được 55% dung lượng sử dụng trong th&aacute;ng qua.&nbsp;</p>\r\n\r\n<p>Để k&iacute;ch hoạt chế độ Tr&igrave;nh tiết kiệm dữ liệu: Chrome &gt; C&agrave;i đặt &gt; Tr&igrave;nh tiết kiệm dữ liệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/bat-mi-5-tien-ich-tuyet-voi-co-san-tren-android.jpg" /></p>\r\n\r\n<p><em>Google Chrome với tiện &iacute;ch tiết kiệm dữ liệu.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tr&igrave;nh tiết kiệm pin c&oacute; sẵn tr&ecirc;n thiết bị</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay tr&ecirc;n kho ứng dụng Play Store c&oacute; rất nhiều ứng dụng tiết kiệm pin nhưng hiệu quả chỉ bằng con số 0, h&atilde;y sử dụng tr&igrave;nh tiết kiệm pin c&oacute; sẵn m&agrave; Google đ&atilde; t&iacute;ch hợp tr&ecirc;n hệ điều h&agrave;nh Android hoặc tr&igrave;nh tiết kiệm pin c&oacute; sẵn tr&ecirc;n m&aacute;y được nh&agrave; sản xuất tối ưu trước đ&oacute;.</p>\r\n\r\n<p>Để thiết lập: C&agrave;i đặt &gt; Pin &gt; Mục 3 chấm &gt; Tr&igrave;nh tiết kiệm pin.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/bat-mi-5-tien-ich-tuyet-voi-co-san-tren-android-2.jpg" />&nbsp;</p>\r\n\r\n<p><em>Tr&igrave;nh tiết kiệm pin c&oacute; sẵn tr&ecirc;n thiết bị.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi bạn đ&atilde; thiết lập tr&igrave;nh tiết kiệm Pin, thiết bị c&oacute; những thay đổi nhất định khi bật ứng dụng n&agrave;y. Cụ thể l&agrave;:</p>\r\n\r\n<ul>\r\n	<li>Phần đầu v&agrave; phần cuối m&agrave;n h&igrave;nh của thiết bị sẽ chuyển sang m&agrave;u cam</li>\r\n	<li>Kh&ocirc;ng thể sử dụng Điều hướng của Google Maps</li>\r\n	<li>Hạn chế rung, dịch vụ vị tr&iacute; v&agrave; hầu hết dữ liệu nền</li>\r\n	<li>Kh&ocirc;ng thể cập nhật email, nhắn tin v&agrave; đồng bộ h&oacute;a c&aacute;c ứng dụng kh&aacute;c, cho tới khi bạn mở c&aacute;c ứng dụng đ&oacute;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ứng dụng Google Now trợ l&yacute; ảo th&ocirc;ng minh&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với Google Now bạn c&oacute; thể t&igrave;m nhanh th&ocirc;ng tin một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng bởi khả năng nhận diện được ch&iacute;nh x&aacute;c &acirc;m thanh cực tốt. Ngo&agrave;i ra n&oacute; c&ograve;n c&oacute; thể tương t&aacute;c với c&aacute;c ứng dụng tr&ecirc;n điện thoại bằng c&aacute;ch ra lệnh như đặt giờ b&aacute;o thức, mở nhanh Facebook, tắt wifi, ph&aacute;t nhạc theo y&ecirc;u cầu....</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/bat-mi-5-tien-ich-tuyet-voi-co-san-tren-android-3.jpg" />&nbsp;</p>\r\n\r\n<p><em>Ứng dụng Google Now trợ l&yacute; ảo th&ocirc;ng minh.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&agrave;ng sử dụng bạn sẽ c&agrave;ng th&iacute;ch th&uacute; bởi t&iacute;nh th&ocirc;ng minh v&agrave; nhạy b&eacute;n trong qu&aacute; xử l&yacute; c&aacute;c y&ecirc;u cầu của m&igrave;nh đề ra, c&ograve;n nếu kh&ocirc;ng biết th&igrave; n&oacute; sẽ đưa y&ecirc;u cầu của bạn tới phần t&igrave;m kiếm tr&ecirc;n web Google Chrome.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chặn một số ứng dụng kh&ocirc;ng cần thiết tr&ecirc;n thiết bị.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n hệ điều h&agrave;nh Android 6.0 Marshmallows c&oacute; một c&ocirc;ng cụ cho ph&eacute;p chặn một số quyền m&agrave; ứng dụng đ&oacute; muốn truy cập v&agrave;o. H&atilde;y loại bỏ quyền những quyền kh&ocirc;ng cần thiết m&agrave; ứng dụng đ&oacute; muốn truy cập.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/bat-mi-5-tien-ich-tuyet-voi-co-san-tren-android-4.jpg" />&nbsp;</p>\r\n\r\n<p><em>Chặn một số ứng dụng kh&ocirc;ng cần thiết tr&ecirc;n thiết bị.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lưu &yacute; kh&ocirc;ng n&ecirc;n tắt c&aacute;c quyền m&agrave; truy cập m&agrave; l&agrave; chức năng ch&iacute;nh của ứng dụng đ&oacute;. V&iacute; dụ như ứng dụng Camera 360 bạn c&oacute; thể tắt vị tr&iacute;, điện thoại&hellip; nhưng tuyệt đối kh&ocirc;ng được chặn quyền truy cập m&aacute;y ảnh, bởi như thế bạn sẽ kh&ocirc;ng thể sử dụng ứng dụng camera 360 để chụp ảnh nữa rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Một tr&ograve; chơi ẩn giấu bởi Google</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chắc hẳn c&oacute; kh&aacute; người chưa biết tr&ecirc;n điện thoại Android phi&ecirc;n bản 5.0 Lollipop trở đi c&oacute; một tr&ograve; chơi ẩn lấy từ &yacute; tưởng người Việt đ&oacute; l&agrave; Flappy Bird của Nguyễn H&agrave; Đ&ocirc;ng, tuy nhi&ecirc;n độ kh&oacute; gấp đ&ocirc;i. Bạn c&oacute; thể trải nghiệm tr&ograve; chơi bằng c&aacute;ch sau:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&agrave;i đặt &gt; Giới thiệu về điện thoại &gt; Nhấp nhiều lần v&agrave;o phi&ecirc;n bản Android &gt; Nhấn nhiều lần v&agrave;o biểu tượng v&agrave; một lần nhấn cứng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="https://img.trananh.com.vn/trananh/2016/12/29/bat-mi-5-tien-ich-tuyet-voi-co-san-tren-android-5.jpg" />&nbsp;</p>\r\n\r\n<p><em>Tr&ograve; chơi ẩn giấu bởi Google.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những tiện &iacute;ch được google t&iacute;ch hợp sẵn tr&ecirc;n Android gi&uacute;p người d&ugrave;ng c&oacute; những trải nghiệm tr&ecirc;n điện thoại tốt hơn. Hi vọng b&agrave;i viết mang đ&atilde; mang lại th&ocirc;ng tin hữu &iacute;ch cho bạn!</p>', 1, '2017-01-23 11:28:40', 65, NULL, '', NULL, NULL, NULL, NULL),
